import React, { useState } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn, MDBNotification } from 'mdb-react-ui-kit';
import { useDispatch } from 'react-redux';
import { updateFormData } from '../slices/formReducer';

const Register = () => {
  const dispatch = useDispatch();

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    img: ''
  });

  const [formErrors, setFormErrors] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    img: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImgChange = (e) => {
    setFormData({ ...formData, img: e.target.files[0] });
  };

  const validateForm = () => {
    setFormErrors({
      name: !formData.name
        ? 'Name is required'
        : /\s/.test(formData.name)
        ? 'Name should not contain spaces'
        : '',
      email: !formData.email
        ? 'Email is required'
        : !/\S+@\S+\.\S+/.test(formData.email)
        ? 'Email should be in email format'
        : '',
      password: !formData.password
        ? 'Password is required'
        : formData.password.length < 8
        ? 'Password should be at least 8 characters'
        : formData.password.length > 12
        ? 'Password should be at most 12 characters'
        : '',
      confirmPassword: !formData.confirmPassword
        ? 'Confirm Password is required'
        : formData.password !== formData.confirmPassword
        ? 'Confirm Password should match the password'
        : '',
      img: !formData.img ? 'Image is required' : ''
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    validateForm();
    dispatch(updateFormData(formData));
    console.log(formData);
    console.log(formData.password.length > 8);
  };

  return (
    <MDBContainer>
      <h3 className="mt-5 mb-4">Register Page</h3>
      <MDBRow>
        <MDBCol md="6">
          <form onSubmit={handleSubmit}>
            <MDBInput label="Name" type="text" name="name" value={formData.name} onChange={handleChange} />
            {formErrors.name && <MDBNotification body={formErrors.name} />}
            
            <MDBInput label="Email" type="email" name="email" value={formData.email} onChange={handleChange} />
            {formErrors.email && <MDBNotification body={formErrors.email} />}
            
            <MDBInput label="Password" type="password" name="password" value={formData.password} onChange={handleChange} />
            {formErrors.password && <MDBNotification body={formErrors.password} />}
            
            <MDBInput label="Confirm Password" type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} />
            {formErrors.confirmPassword && <MDBNotification body={formErrors.confirmPassword} />}
            
            <MDBInput label="Upload Image" type="file" name="img" onChange={handleImgChange} />
            {formErrors.img && <MDBNotification body={formErrors.img} />}
            
            <MDBBtn color="primary" type="submit">
              Signup
            </MDBBtn>
          </form>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default Register;
